$(document).ready(function(){$(".characters-slider_content").slick({slidesToShow:1,slidesToScroll:1,infinite:!0,cssEase:"linear"}),$(".characters-slider a").on("click",function(e){e.preventDefault();var c=$(this).data("character");$(".characters-slider").fadeOut(),$("#"+c).fadeIn()}),$(".character-view a.close-btn").on("click",function(e){e.preventDefault(),$(this).closest(".character-view").slideUp(),$(".characters-slider").slideDown()})});